class Solution(object):
    def islandPerimeter(self, grid):
        ans = 0
        h = len(grid)
        w = len(grid[0]) if h else 0
        for x in range(h):
            for y in range(w):
                if grid[x][y] == 1:
                    ans += 4
                    if x > 0 and grid[x-1][y]:
                        ans -= 2
                    if y > 0 and grid[x][y-1]:
                        ans -= 2
        return ans
